import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Button } from "./components/Button";
import { Upload } from "./SecurityActions"
import "./UploadFiles.css";
import InNavbar from './InNavbar';
class UploadFiles extends Component {
  constructor() {
    super();

    this.state = {
      docName: "",
      doctype: "",
      docData: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  //life cycle hooks
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  onChange(e) {
    this.setState({ docdata: e.target.files[0] });;
  }

  onSubmit(e) {
    e.preventDefault();
    const UploadRequest = {
      docName: this.state.docName,
    doctype: this.state.doctype,
      docData:this.state.docData
    };
    this.props.Upload(UploadRequest, this.props.history);
  }

  render() {

    return (
        <>
        <InNavbar />
          <div className="frame">
          <div className="center">
              <div className="title">
                  <h1>Drop file to upload</h1>
              </div>
      
              <div className="dropzone" >
                  <img src="http://100dayscss.com/codepen/upload.svg" className="upload-icon" alt='uploadimg' />
                    <input
                    className="upload-input"
                      type="file"
                      name="docName"
                      value={this.state.docName}
                      onChange={this.onChange}
                    />
                    <Button onClick={this.onSubmit}>Upload file</Button></div></div></div>
        </>          
                  
    );
  }
}

UploadFiles.propTypes = {
  Upload: PropTypes.func.isRequired,
 // errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { Upload }
)(UploadFiles);
