import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Button } from "./components/Button";
import { Upload } from "./SecurityActions"
import "./UploadFiles.css";
import InNavbar from './InNavbar';
class UploadFiles extends Component {
    constructor() 
    {
      super();
      
        this.onFileChange = this.onFileChange.bind(this);
          this.onFileUpload= this.onFileUpload.bind(this);
      };
      componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
          this.setState({ errors: nextProps.errors });
        }
      }
  onFileChange(e) {
  this.setState({ docdata: e.target.files[0] });}
  onFileUpload = (e) => {
    e.preventDefault();
      const formData = new FormData();
      formData.append(
          
        this.state.docdata.name,
        this.state.docdata,
        this.state.docdata.type
        
      );
      this.props.Upload(formData)
    };
    fileData = () => {
        if (this.state.docdata) {
    return (
            <div>
              <h2>File Details:</h2>
               <p>File Name: {this.state.docdata.name}</p>
               <p>File Type: {this.state.docdata.type}</p>
               <p>Last Modified:{" "}
                  {this.state.docdata.lastModifiedDate.toDateString()}
                </p>
                </div>
            );
          } else {
            return (
              <div>
                <br />
                <h4>Choose before Pressing the Upload button</h4>
              </div>
            );
          }
        };  
  
    render(){
       
  return (
  <>
  <InNavbar />
    <div className="frame">
	<div className="center">
		<div className="title">
			<h1>Drop file to upload</h1>
		</div>

		<div className="dropzone" >
			<img src="http://100dayscss.com/codepen/upload.svg" className="upload-icon" alt='uploadimg' />
			<input type="file" className="upload-input" onChange={this.onFileChange} />
		</div>
	</div>
    <Button onClick={this.onFileUpload}>Upload file</Button>

</div>
</>
  )
}
}
UploadFiles.propTypes = {
  Upload: PropTypes.func.isRequired,
 // errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { Upload }
)(UploadFiles);
