import React, { Component } from 'react'
import './UploadFiles.css';
import { Button } from './components/Button';
import InNavbar from './InNavbar';
import axios from "axios";
export default class UploadFiles extends Component {
    constructor() 
  {
    super();
    this.state = {
     docname:"",
     doctype:"",
    data: null,
      }
      this.onFileChange = this.onFileChange.bind(this);
        this.onFileUpload = this.onFileUpload.bind(this);
    };
onFileChange(e) {
this.setState({ data: e.target.files[0] });}
onFileUpload = () => {
    const formData = new FormData();
    formData.append(
      this.state.data.name,
      this.state.data.type,
      this.state.data
    );
    console.log(this.state.data);

    axios.post("http://localhost:9090/api/users/addDocument", formData);
  };
      
  fileData = () => {
    if (this.state.data) {
return (
        <div>
          <h2>File Details:</h2>
           <p>File Name: {this.state.data.name}</p>
           <p>File Type: {this.state.data.type}</p>
           <p>Last Modified:{" "}
              {this.state.data.lastModifiedDate.toDateString()}
            </p>
            </div>
        );
      } else {
        return (
          <div>
            <br />
            <h4>Choose before Pressing the Upload button</h4>
          </div>
        );
      }
    };
  
    render(){
  return (
  <>
  <InNavbar />
    <div className="frame">
	<div className="center">
		<div className="title">
			<h1>Drop file to upload</h1>
		</div>

		<div className="dropzone" >
			<img src="http://100dayscss.com/codepen/upload.svg" className="upload-icon" alt='uploadimg' />
			<input type="file" className="upload-input" onChange={this.onFileChange} />
		</div>

		<Button onClick={this.onFileUpload}>Upload file</Button>

	</div>
    {this.fileData()}
</div>
</>
  )
}
}
